function [ster,output]=xct(x,y,lag)%Function [ster,output]=xct(x,y,lag)%Crosscorrelations between vectors x (input) and y (output).%Uses sterdofinoutcr and Xcbg.composfil=[x,y]; [m,n]=size(composfil); in=[1]; nn=length(in);out=[2];lagrow=2*lag+1;cor=zeros(lagrow,nn+1);		for indx=1:nn; [ccxy,gain] = Xcbg(lag,composfil(:,in(indx)),composfil(:,out(indx)));cor(:,1)=ccxy(:,2);gn(:,1)=gain(:,2);[stcr,dofin,dofout,dofcr] = sterdofinoutcr(composfil(:,in(indx)),composfil(:,out(indx)),m-1);cor(:,2)=ccxy(:,2)/stcr;ster=[stcr,dofin,dofout,dofcr];output=[ccxy(:,1),cor(:,1),cor(:,2),gn(:,1)];		end	